document.addEventListener('DOMContentLoaded',function(){const submitBtn=document.querySelector('#submitBtn');const resendBtn=document.querySelector('#resendBtn');const resendText=document.querySelector('#resendText');document.querySelector('#otpForm').addEventListener('submit',function(){submitBtn.disabled=true;submitBtn.innerHTML='<i class="fas fa-spinner fa-spin me-2"></i>در حال بررسی...';});document.querySelector('#resendForm').addEventListener('submit',function(e){resendBtn.disabled=true;resendText.innerHTML='<i class="fas fa-spinner fa-spin me-2"></i>در حال ارسال...';});const remainingSeconds=parseInt(resendBtn.getAttribute('data-remaining-seconds'))||0;if(remainingSeconds>0){let timeLeft=remainingSeconds;resendBtn.disabled=true;resendText.textContent=`صبر کنید (${timeLeft} ثانیه)`;const timer=setInterval(()=>{timeLeft--;resendText.textContent=`صبر کنید (${timeLeft} ثانیه)`;if(timeLeft<=0){clearInterval(timer);resendBtn.disabled=false;resendText.textContent='ارسال مجدد کد';}},1000);}});;