document.addEventListener('DOMContentLoaded',()=>{const observerOptions={threshold:0.2};const observer=new IntersectionObserver((entries,observer)=>{entries.forEach(entry=>{if(entry.isIntersecting){entry.target.classList.add('animate__animated');if(entry.target.classList.contains('doctor-card')){entry.target.classList.add('animate__fadeInUp');}else if(entry.target.classList.contains('service-card')){entry.target.classList.add('animate__zoomIn');}
observer.unobserve(entry.target);}});},observerOptions);document.querySelectorAll('.doctor-card, .service-card').forEach(card=>{observer.observe(card);});const cards=document.querySelectorAll('.hover-effect');cards.forEach(card=>{card.addEventListener('mouseenter',()=>{card.style.transform='translateY(-10px)';card.style.transition='transform 0.3s ease';});card.addEventListener('mouseleave',()=>{card.style.transform='translateY(0)';});});const socialLinks=document.querySelectorAll('.social-links a');socialLinks.forEach((link,index)=>{link.style.opacity='0';setTimeout(()=>{link.style.transition='opacity 0.5s ease';link.style.opacity='1';},index*200);});});;