document.addEventListener('DOMContentLoaded',function(){const togglePassword=document.querySelector('.toggle-password');const passwordInput=document.querySelector('#id_password');const toggleIcon=document.querySelector('#toggleIcon');if(!togglePassword||!passwordInput||!toggleIcon){console.error('یکی از المنت‌ها پیدا نشد:',{togglePassword:togglePassword,passwordInput:passwordInput,toggleIcon:toggleIcon});return;}
togglePassword.addEventListener('click',function(){const type=passwordInput.getAttribute('type')==='password'?'text':'password';passwordInput.setAttribute('type',type);toggleIcon.classList.toggle('fa-eye');toggleIcon.classList.toggle('fa-eye-slash');});});;