document.addEventListener('DOMContentLoaded',function(){document.querySelectorAll('.edit-btn').forEach(button=>{button.addEventListener('click',function(){const id=this.getAttribute('data-id');document.getElementById(`row-${id}`).style.display='none';document.getElementById(`edit-form-${id}`).style.display='table-row';});});document.querySelectorAll('.cancel-btn').forEach(button=>{button.addEventListener('click',function(){const id=this.getAttribute('data-id');document.getElementById(`row-${id}`).style.display='table-row';document.getElementById(`edit-form-${id}`).style.display='none';});});});;