var map=new L.Map('map',{attributionControl:false,key:'web.567de89946f24e1ab41c2931720f1871',maptype:'osm-bright',center:[37.106763111365744,58.51246833914425],zoom:15,zoomControl:true,});L.marker([37.106763111365744,58.51246833914425]).addTo(map).bindPopup('مکان مطب').openPopup();document.addEventListener('DOMContentLoaded',()=>{const form=document.querySelector('.needs-validation');if(form){form.addEventListener('submit',(event)=>{if(!form.checkValidity()){event.preventDefault();event.stopPropagation();form.classList.add('was-validated');}},false);const inputs=form.querySelectorAll('.form-control');inputs.forEach(input=>{input.addEventListener('blur',()=>{if(!input.checkValidity()&&!input.classList.contains('shake')){input.classList.add('shake');setTimeout(()=>input.classList.remove('shake'),300);}});});}
const decorativeTexts=document.querySelectorAll('.decorative-text');if(decorativeTexts.length>0){decorativeTexts.forEach((text,index)=>{const delay=index*200;setTimeout(()=>{text.classList.add('wiggle');setInterval(()=>{text.classList.toggle('wiggle');},3000+Math.random()*2000);},delay);});}});const debounce=(func,wait)=>{let timeout;return(...args)=>{clearTimeout(timeout);timeout=setTimeout(()=>func.apply(this,args),wait);};};const buttons=document.querySelectorAll('.btn');buttons.forEach(btn=>{btn.addEventListener('mouseenter',debounce(()=>{btn.classList.add('hover-scale');},100));btn.addEventListener('mouseleave',debounce(()=>{btn.classList.remove('hover-scale');},100));});;